C++app/src/main/cpp/native-lib.cpp:
```
#include <jni.h>
#include "inc/fmod.hpp"
#include <string>

#include <unistd.h>

using namespace FMOD;

#define MODE_NORMAL 0
#define MODE_LUOLI 1
#define MODE_DASHU 2
#define MODE_JINGSONG 3
#define MODE_GAOGUAI 4
#define MODE_KONGLING 5

#include <android/log.h>

#define LOGI(FORMAT, ...) __android_log_print(ANDROID_LOG_INFO,"voiceChange",FORMAT,##__VA_ARGS__);
#define LOGE(FORMAT, ...) __android_log_print(ANDROID_LOG_ERROR,"voiceChange",FORMAT,##__VA_ARGS__);

extern "C"
JNIEXPORT void JNICALL
Java_com_exp_fmodvoicechange_Util_voiceChange(JNIEnv *env, jclass cls, jstring path_str, jint type) {
    System * system;
    Sound * sound;
    Channel  *channel;
    DSP *dsp;
    bool playing= true;
    float frequency=1;

    //
    System_Create(&system);
    system->init(32, FMOD_INIT_NORMAL, NULL);

    //stringchar*
    const char* path=env->GetStringUTFChars(path_str,NULL);

    //
    system->createSound(path,FMOD_DEFAULT, 0, &sound);
    try {
        //
        switch (type) {
            case MODE_NORMAL:
                //
                system->playSound(sound, 0, false, &channel);
                break;
            case MODE_LUOLI:
                //
                //DSP digital signal process
                //dsp ->  fmod
                //FMOD_DSP_TYPE_PITCHSHIFT dsp
                system->createDSPByType(FMOD_DSP_TYPE_PITCHSHIFT, &dsp);
                //
                dsp->setParameterFloat(FMOD_DSP_TYPE_PITCHSHIFT, 2.5);
                //
                system->playSound(sound, 0, false, &channel);
                //channeldsp
                channel->addDSP(0, dsp);
                break;
            case MODE_DASHU:
                //
                system->createDSPByType(FMOD_DSP_TYPE_PITCHSHIFT, &dsp);
                //
                dsp->setParameterFloat(FMOD_DSP_PITCHSHIFT_PITCH, 0.8);
                //
                system->playSound(sound, 0, false, &channel);
                //channeldsp
                channel->addDSP(0, dsp);
                break;
            case MODE_JINGSONG:
                //
                system->createDSPByType(FMOD_DSP_TYPE_TREMOLO, &dsp);
                dsp->setParameterFloat(FMOD_DSP_TREMOLO_SKEW, 0.5);
                system->playSound(sound, 0, false, &channel);
                channel->addDSP(0, dsp);
                break;
            case MODE_GAOGUAI:
                //
                //
                system->playSound(sound, 0, false, &channel);
                //frequency  
                channel->getFrequency(&frequency);
                frequency = frequency * 1.6;
                channel->setFrequency(frequency);
                break;
            case MODE_KONGLING:
                //
                system->createDSPByType(FMOD_DSP_TYPE_ECHO, &dsp);
                dsp->setParameterFloat(FMOD_DSP_ECHO_DELAY, 300);
                dsp->setParameterFloat(FMOD_DSP_ECHO_FEEDBACK, 20);
                system->playSound(sound, 0, false, &channel);
                channel->addDSP(0, dsp);
                break;
            default:
                break;
        }
    }
    catch (...){
        //
        LOGE("%s","");
    }
    system->update();
    //
    //
    //
    while(playing){
        channel->isPlaying(&playing);
        usleep(1000 * 1000);
    }

    //
    sound->release();
    system->close();
    system->release();
    env->ReleaseStringUTFChars(path_str,path);
}
